import React, {Fragment, useState} from "react"

let labels=[], datasets=[], xAxis="";
const FileUpload = ()=>{

     const [file,setFile] = useState();

      const createJson = (text)=>{
          const headers = text.slice(0,text.indexOf('\n')).split(',');
          const rows = text.slice(text.indexOf('\n')+1,text.length).split('\n');
          
          xAxis = headers[0];
          for(let i=0;i<rows.length;i++){
               const rowData = rows[i].split(",");
               labels.push(rowData[0]);
          }
          
          const arr  = rows.map(row=>{
               const values = row.split(",");
               const eachObject = headers.reduce((obj,header,i)=>{
                    obj[header]=values[i];
                    return obj;
               },{});
               return eachObject;
          })
            
          for(let i=0;i<labels.length;i++){
               const points = [];
               for(let j=0;j<arr.length;j++){
                    points.add(arr[j].labels[i])
               }
               const obj = {"label": labels[i],"data":points};
               datasets.add(obj);
          }
          
           console.log(arr[0]);
      }
      const readFile = ()=>{
           const uploadedFile = file;
           console.log(file);
           if(uploadedFile!=null){
               const reader = new FileReader();
               reader.onload=(e)=>{
                    createJson(e.target.result);
               }
               reader.readAsText(uploadedFile);
           }
      }
     
     return <Fragment>
           
           <form id="file-form">
           <input type="file" accept=".csv"  id="inputGroupFile02" onChange={e=>setFile(e.target.files[0])}/>
           <button type="submit" onClick={(e)=>{
                e.preventDefault();
                if(file)
                readFile();
           }}>Submit</button>
           </form>

             
           
     </Fragment>
}

export default FileUpload;
