import React, {Fragment, useState} from "react"

let labels=[], datasets=[], xAxis="";
const FileUpload = ()=>{

     const [file,setFile] = useState();

      const createJson = (text)=>{
          const headers = text.slice(0,text.indexOf('\n')).split(',');
          const row = text.slice(text.indexOf('\n')+1,text.length).split('\n');
          console.log(text);
          
          xAxis = headers[0];
          for(let i=0;i<row.length;i++){
               const rowData = row[i].split(",");
               labels.push(rowData[0]);
          }
          
          for(let i=1;i<headers.length;i++){
               let obj = {label:headers[i],
                          data:[]};
               datasets.push(obj);
          }
          
          for(let i=1;i<datasets.length;i++){
               for(let j=0;j<row.length;j++){
               const rowData = row[j].split(",");
                    //datasets[i].data.push(rowData[j+1][i]);
                    console.log(rowData[j]);
               }
          }

          console.log(datasets);

      }
      const readFile = ()=>{
           const uploadedFile = file;
           console.log(file);
           if(uploadedFile!=null){
               const reader = new FileReader();
               reader.onload=(e)=>{
                    createJson(e.target.result);
               }
               reader.readAsText(uploadedFile);
           }
      }
     
     return <Fragment>
           
           <form id="file-form">
           <input type="file" accept=".csv"  id="inputGroupFile02" onChange={e=>setFile(e.target.files[0])}/>
           <button type="submit" onClick={(e)=>{
                e.preventDefault();
                if(file)
                readFile();
           }}>Submit</button>
           </form>

             
           
     </Fragment>
}

export default FileUpload;
