import { Bar , Line, Pie } from "react-chartjs-2";
import Chart from 'chart.js/auto';
  
const colors = ["#D291BC", "#B8DBD0", "#FFAF68", "#E8A68E","9BB8ED","A39FE1","B3D9B2"];
 

function createLabels(arr){
       if(arr!=undefined){
        let labels = [];
        for(let i=1;i<arr.length;i++)
              if(arr[i][0].length!=0)
            labels.push(arr[i][0]);
         return (labels); 
       }
       
}

let options = {
  maintainAspectRatio: false,
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: true,
      text: "graph",
    },
  },
};

function createDataSets(arr){
      
      let datasetsArray= [];
      for(let i=1;i<arr[0].length;i++){
            let dataArray = [];
            for(let j=1;j<arr.length;j++){
                if(arr[j][i]!=undefined)
                dataArray.push(arr[j][i]);
            }
            
            let datasetObj = {
                label: arr[0][i],
                data: dataArray,
                borderColor:"#000",
                borderWidth:"1.5",
                backgroundColor:colors[i],
                hoverBorderWidth:"2"
            }
            datasetsArray.push(datasetObj);
      }
      return datasetsArray;
}

function createOptions(){
       
}
const Graph = (props)=>{
 
       let actualData = {
        labels:createLabels(props.chartData),
        datasets: createDataSets(props.chartData),
        backgroundColor: colors[0]
       }
    
    
    return <Bar data={actualData} options={options} />
    
    
}

export default Graph;
