import React, {Fragment, useState} from "react"

const FileUpload = ()=>{

     const [file,setFile] = useState();

      const createJson = (text)=>{
          const headers = text.slice(0,text.indexOf('\n')).split(',');
          const rowNumber = text.slice(text.indexOf('\n')+1,text.length).split('\n');
          console.log(rowNumber[0]);
          console.log(text);
      }
      const readFile = ()=>{
           const uploadedFile = file;
           console.log(file);
           if(uploadedFile!=null){
               const reader = new FileReader();
               reader.onload=(e)=>{
                    createJson(e.target.result);
               }
               reader.readAsText(uploadedFile);
           }
      }
     
     return <Fragment>
           
           <form id="file-form">
           <input type="file" accept=".csv"  id="inputGroupFile02" onChange={e=>setFile(e.target.files[0])}/>
           <button type="submit" onClick={(e)=>{
                e.preventDefault();
                if(file)
                readFile();
           }}>Submit</button>
           </form>

             
           
     </Fragment>
}

export default FileUpload;
