function readFiles(file){
     if(file==null)
     return {};
     else{
        const reader = new FileReader();
        let text = "";
        reader.onload = (e)=>{
              console.log(e.target.result);
        }

        reader.readAsText(file);
        return text;
     }
}

export default readFiles;
