import React, {Fragment, useState} from "react";
import {Line} from "react-chartjs-2";


import {
     Chart as ChartJS,
     CategoryScale,
     LinearScale,
     BarElement,
     Title,
     Tooltip,
     Legend,
     PointElement,
     LineElement,
   } from 'chart.js';
 
   ChartJS.register(
     CategoryScale,
     LinearScale,
     BarElement,
     Title,
     Tooltip,
     Legend,
     PointElement,
     LineElement
   );
 const options={
     responsive: true,
   plugins: {
     legend: {
       position: 'top',
     },
     title: {
       display: true,
       text: 'Chart.js Line Chart',
     },
   },
 }

let labels=[], datasets=[], xAxis="";
const FileUpload = ()=>{

     const [file,setFile] = useState();

      const createJson = (text)=>{
          const headers = text.slice(0,text.indexOf('\n')).split(',');
          const rows = text.slice(text.indexOf('\n')+1,text.length).split('\n');
          
          const colNumber = headers.length;
          const rowNumber = rows.length;

          for(let i=1;i<colNumber;i++){
               const arr = [];
               for(let j=0;j<rowNumber;j++){
                    arr.push(rows[j].split(",")[i]);
               }
               const obj = {"label":headers[i],"data":arr};
               datasets.push(obj);
          }

          for(let j=0;j<rowNumber;j++)
          labels.push(rows[j].split(",")[0]);

          xAxis= headers[0];
          console.log(xAxis);
          
      }
      const readFile = ()=>{
           const uploadedFile = file;
           console.log(file);
           if(uploadedFile!=null){
               const reader = new FileReader();
               reader.onload=(e)=>{
                    createJson(e.target.result);
               }
               reader.readAsText(uploadedFile);
           }
      }
     
     return <Fragment>
           
           <form id="file-form">
           <input type="file" accept=".csv"  id="inputGroupFile02" onChange={e=>setFile(e.target.files[0])}/>
           <button type="submit" onClick={(e)=>{
                e.preventDefault();
                if(file)
                readFile();
           }}>Submit</button>
           </form>

             
           
     </Fragment>
}

export default FileUpload;
