import React, {Fragment, useState} from "react";
import {Line} from "react-chartjs-2";


import {
     Chart as ChartJS,
     CategoryScale,
     LinearScale,
     BarElement,
     Title,
     Tooltip,
     Legend,
     PointElement,
     LineElement,
   } from 'chart.js';
 
   ChartJS.register(
     CategoryScale,
     LinearScale,
     BarElement,
     Title,
     Tooltip,
     Legend,
     PointElement,
     LineElement
   );
 const options={
     responsive: true,
   plugins: {
     legend: {
       position: 'top',
     },
     title: {
       display: true,
       text: 'Chart.js Line Chart',
     },
   },
 }
 const colors = ["#D291BC","#D291BC","#D291BC","#E8A68E"];
 let labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];

const data = {
    labels,
    datasets: [
      {
        label: 'Dataset 1',
        data: labels.map(() => Math.random()*1000+1),
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
      {
        label: 'Dataset 2',
        data: labels.map(() => Math.random()*1000+1),
        borderColor: 'rgb(53, 162, 235)',
        backgroundColor: 'rgba(53, 162, 235, 0.5)',
      },
    ],
  };

let labels1=[], datasets=[], xAxis="";
const FileUpload = ()=>{

     labels1=[];
     datasets=[];
     xAxis="";
     const [file,setFile] = useState();
     const [chartData,setChartData]=useState(data);

      const createJson = (text)=>{
          const headers = text.slice(0,text.indexOf('\n')).split(',');
          const rows = text.slice(text.indexOf('\n')+1,text.length).split('\n');
          
          const colNumber = headers.length;
          const rowNumber = rows.length;

          for(let i=1;i<colNumber;i++){
               const arr = [];
               for(let j=0;j<rowNumber;j++){
                    arr.push(rows[j].split(",")[i]);
               }
               const obj = {label:headers[i],
                            data:arr,
                            borderColor: colors[i-1],
                            backgroundColor: colors[i-1]};
               datasets.push(obj);
          }

          for(let j=0;j<rowNumber;j++)
          labels1.push(rows[j].split(",")[0]);

          xAxis= headers[0];
          console.log(xAxis);
          
          const finalObject = {labels:labels1,datasets};
          console.log(finalObject);
          setChartData(finalObject);
      }


      const readFile = ()=>{
           const uploadedFile = file;
           console.log(file);
           if(uploadedFile!=null){
               const reader = new FileReader();
               reader.onload=(e)=>{
                    createJson(e.target.result);
               }
               reader.readAsText(uploadedFile);
           }
      }
     
     return <Fragment>
           
           <form id="file-form">
           <input type="file" accept=".csv"  id="inputGroupFile02" onChange={e=>setFile(e.target.files[0])}/>
           <button type="submit" onClick={(e)=>{
                e.preventDefault();
                if(file)
                readFile();
           }}>Submit</button>
           </form>
           <Line options={options} data={chartData} height={1000} width={2000}/>
             
           
     </Fragment>
}

export default FileUpload;
