              if(arr[i][0].length!=0)
            labels.push(arr[i][0]);
         return (labels); 
       }
       
}


function createDataSets(arr){
      let datasetsArray= [];
      for(let i=1;i<arr[0].length;i++){
            let dataArray = [];
            for(let j=1;j<arr.length;j++){
                if(arr[j][i]!=undefined)
                dataArray.push(arr[j][i]);
            }
            let datasetObj = {
                labels: arr[0][i],
                data: dataArray
            }
            datasetsArray.push(datasetObj);
      }
      return datasetsArray;
}

